# Given two strings s1 and s2, write a function to return true if s2 contains the permutation of s1. In other words,
# one of the first string's permutations is the substring of the second string.

class Solution(object):
    def checkInclusion(self, s1, s2):
        """
        :type s1: str
        :type s2: str
        :rtype: bool
        """
        frequency_map = {}
        for i in range(0, len(s1)):
            character = s1[i]
            if character not in frequency_map:
                frequency_map[character] = 1
            else:
                frequency_map[character] += 1

        #print(frequency_map)
        end = 0
        begin = 0
        count = len(frequency_map)
        length_of_big_string = len(s2)

        while end < length_of_big_string:
            character = s2[end]

            if character in frequency_map:
                frequency_map[character] -= 1
                if frequency_map[character] == 0:
                    count -= 1
            end += 1

            while count == 0:
                begin_char = s2[begin]

                if end - begin == len(s1):
                    return True

                if begin_char in frequency_map:
                    frequency_map[begin_char] += 1

                    if frequency_map[begin_char] > 0:
                        count += 1
                begin += 1
        return False

my_sol = Solution()

s1 = "ab"
s2 = "eidbaooo"
print(my_sol.checkInclusion(s1, s2)) #True

